import React from "react";
import { Component } from 'react';
import './Detail.css';


class Detail extends Component {

    render() {
        return (
            <div class="page_container">
              <h1>상세페이지</h1>
              <div class="page_left">
                <h2>{this.props.names[this.props.id]}</h2>
                <img src={this.props.images[this.props.id]} alt="Ako-image" width="250" height="250"/>
                <br/>
                <hr class="line"></hr>
                current price<br/>
              </div>
        
              <div class="page_right">
                <br/><br/><br/>
                <h3>Description</h3>
                <div class="description_box">
                {this.props.descriptions[this.props.id]}
                </div>
                <br/>
              </div>
        
              
              <div class="price">
                <img src="./images/eth.png" alt='eth-icon' width="15" height="25"/>
                &nbsp;
                {this.props.price}
              </div>
        
              <div class="buy">
                <button onClick={
                    this.props.plusLike(this.props.id+1)
                   
                    }>❤️</button>
                <div>{this.props.likes[this.props.id]}</div>
                &nbsp;
                <button>
                  buy
                </button>
              </div>
              
              
            </div>
          );
    }
}

export default Detail;

