import React from "react";
import { Component } from 'react';
import './Market.css';

class Market extends Component {

  plus() {
    this.setState({page: this.state.page+1})
  }

  minus() {
    this.setState({page: this.state.page-1})
  }



  render() {
    return (
      <div>
        <button onclick = {this.minus()}>페이지 감소</button>
        <button onclick = {this.plus()}>페이지 증가</button>
        <h3>마켓 임시 페이지입니다.</h3>
        <div className="row text-center">
        {this.props.images.map((uri, key) => {
          return (
            <div key={key} className="col-md-2 mb-3">
              {this.state.page===key && <img className='token' src={uri} alt="token"></img>}
              {this.state.page===key && <div>{this.props.names[key]}</div>}
            </div>
          )
        })}
        </div>
      </div>
    );
  }


  constructor(props) {
    super(props)
    this.state = {
      page: 1
    }
  }

}

export default Market;
