import React from "react";
import { Component } from 'react';
import './Market.css';
import {Link} from 'react-router-dom';

var likes=[];
var count=0;

class Market extends Component {

  async componentWillMount() {
    await this.callCount().then(() => {this.callLikes(count)});
  }

  async callCount() {
    fetch("http://localhost:3001/count", {
      method: "post",
      headers: {
        "content-type": "application/json",
      },
      body: JSON.stringify(),
    })
      .then((res) => res.json())
      .then((json) => {
        count=json.count;
      });
  };
  

  async callLikes(num) {
    for (var j = 1; j <= num; j++) {
      await this.callLike(j);
    }
  }

  async callLike(tokenId) {
      const post = {
        id: tokenId,
      };
      fetch("http://localhost:3001/likes", {
        method: "post",
        headers: {
          "content-type": "application/json",
        },
        body: JSON.stringify(post),
      })
        .then((res) => res.json())
        .then((json) => {
          likes.push(json.likes)
        });
    };


  render() {
    return (
      <div>
        <h3>마켓 임시 페이지입니다.</h3>
        <button onClick={this.callLike}>버튼</button>
        <div className="row text-center" style={{height:'700px'}}>
          {this.props.images.map((uri, key) => {
            return (
              <div key={key} className="col-md-2 mb-3">
                <Link to="/Detail">
                  <div onClick={() => {
                    this.props.idUpdate(key)
                    this.props.priceUpdate(key)}}>
                  {this.props.approved[key] && <img className='token' src={uri} alt="token"></img>}
                  {this.props.approved[key] && <div>{this.props.names[key]}</div>}
                  {<div>{likes[key]}</div>}
                  </div>
                </Link>
              </div>
            )
          })}
        </div>
      </div>
    );
  }
}

export default Market;
