import React, { Component } from "react";
import {Link} from 'react-router-dom';
import './MyCollection.css';

var likes=[];

class MyCollection extends Component {

    async componentWillMount() {
        await this.callLikes();
      }
    
      async callLikes() {
        console.log('callCount 시작');
        var count=0;
        await fetch("http://localhost:3001/count", {
          method: "post",
          headers: {
            "content-type": "application/json",
          },
          body: JSON.stringify(),
        })
          .then((res) => res.json())
          .then((json) => {
            count=json.count;
            console.log(count)
          });
    
        console.log('callLikes 시작');
        for (var j = 1; j <= count; j++) {
          this.callLike(j);
        }
      }
    
      async callLike(tokenId) {
          const post = {
            id: tokenId,
          };
          await fetch("http://localhost:3001/likes", {
            method: "post",
            headers: {
              "content-type": "application/json",
            },
            body: JSON.stringify(post),
          })
            .then((res) => res.json())
            .then((json) => {
              likes.push(json.likes)
              console.log(json.likes)
            });
        };

render(){
    return (
        <div>
            <h3>MyCollection 임시 페이지입니다.</h3>
            <div className="row text-center" style={{height:'600px'}}>
                {this.props.images.map((uri, key) => {
                    return (
                        <div key={key} className="col-md-2 mb-3">
                        <Link to="/Detail">
                          <div onClick={() => {
                                                              this.props.idUpdate(key)
                                                              this.props.priceUpdate(key)}}>
                            {(this.props.owners[key] === this.props.account) && <img className='token' src={uri} alt="token"></img>}
                            {(this.props.owners[key] === this.props.account) && <div>{this.props.names[key]}</div>}
                            {(this.props.owners[key] === this.props.account) && <div>{likes[key]}</div>}
                        </div>
                        </Link>
                                        </div>
                    )
                })}
            </div>
        </div>
    );
}
}

export default MyCollection;
