import React from "react";
import { Component } from 'react';
import './Detail.css';


class Detail extends Component {

    plusLike(tokenId) {
        const post = {
            id: tokenId,
        };
        fetch("http://localhost:3001/plus", {
            method: "post",
            headers: {
                "content-type": "application/json",
            },
            body: JSON.stringify(post),
        })
            .then((res) => res.json())
            .then((json) => {
            });
    };

    render() {
        return (
            <div className="page_container">
                <h1>상세페이지</h1>
                <div className="page_left">
                    <h2>{this.props.names[this.props.id]}</h2>
                    <img src={this.props.images[this.props.id]} alt="Ako-image" width="250" height="250" />
                    <br />
                    <hr className="line"></hr>
                    current price<br />
                </div>

                <div className="page_right">
                    <br /><br /><br />
                    <h3>Description</h3>
                    <div className="description_box">
                        {this.props.descriptions[this.props.id]}
                    </div>
                    <br />
                </div>


                <div className="price">
                    <img src="./images/eth.png" alt='eth-icon' width="15" height="25" />
                    &nbsp;
                    {this.props.price}
                </div>

                <div className="buy">
                    {(this.props.owners[this.props.id] != this.props.account) && <button onClick={() => {
                        this.plusLike(this.props.id + 1)
                    }

                    }>❤️좋아요</button>}


                    &nbsp;
                    <button onClick={() => {
                        this.props.buy(this.props.id + 1)
                    }}>buy</button>


                    <button onClick={() => {
                        this.props.sell(1, 12)
                    }}>sell 1 at 10</button>


                    <button onClick={() => {
                        this.props.changePrice(1, 5)
                    }}>change price 1 to 5</button>


                    <button onClick={() => {
                        this.props.sellCancel(1)
                    }}>cancel sell 1</button>
                </div>


            </div>
        );
    }
}

export default Detail;

