import React from "react";
import { Component } from 'react';
import './Market.css';

let page=0
class Market extends Component {


  plus() {
    if(page<2){
      page+=1;
      console.log(page);
    }

  }

  minus() {
    if(page>0){
    page-=1;
    console.log(page);
    }
  }



  render() {
    return (
      <div>
        <button onClick = {this.minus()}>페이지 감소</button>
        <div>{page}</div>
        <button onClick = {this.plus()}>페이지 증가</button>
        <h3>마켓 임시 페이지입니다.</h3>
        <div className="row text-center">
        {this.props.images.map((uri, key) => {
          return (
            <div key={key} className="col-md-2 mb-3">
              {page===key && <img className='token' src={uri} alt="token"></img>}
              {page===key && <div>{this.props.names[key]}</div>}
            </div>
          )
        })}
        </div>
      </div>
    );
  }
}

export default Market;
