    const post ={
      id : 1,
    };
    fetch("http://localhost:3001/likes",{
      method:"post",
      headers : {
        "content-type" : "application/json",
      },
      body : JSON.stringify(post),
    })
    .then((res)=>res.json())
    .then((json)=>{
      return json
    });
  };

  async call() {
    await this.callLike()
  }


  render() {
    
    return (
      <div>
        <h3>마켓 임시 페이지입니다.</h3>
        <button onClick={this.callLike}>버튼</button>
        <div className="row text-center" style={{height:'700px'}}>
          {this.props.images.map((uri, key) => {
            return (

              <div key={key} className="col-md-2 mb-3">
                <Link to="/Detail">
                  <div onClick={() => {
                    this.props.idUpdate(key)
                    this.props.priceUpdate(key)}}>
